@if(is_array($view->iconFilter))
  <script>
    document.addEventListener("DOMContentLoaded", function () {
      var filters = JSON.parse('{!! json_encode($view->iconFilter) !!}')
      var options = $("#{{$name}} option")
      for (var i = 0; i < options.length; i++) {
        var option = options[i]
        var mustShow = false;
        for (var j = 0; j < filters.length; j++) {
          if ($(option).val() === "fa " + filters[j]) {
            mustShow = true;
          }
        }
        if (!mustShow) {
          $(option).remove()
        }

      }
    });


  </script>


